const createTicketSchema = z.object({
  title: z.string().min(5, "Title must be at least 5 characters").max(100, "Title cannot exceed 100 characters"),
  description: z.string().min(20, "Description must be at least 20 characters"),
  categoryId: z.string().min(1, "Please select a category"),
  subcategoryId: z.string().optional(),
  priority: z.enum(["low", "medium", "high"]).default("medium"),
  supportType: z.enum(["remote", "telephonic", "onsite_visit", "other"]).default("remote"),
  contactEmail: z.string().email("Please enter a valid email address"),
  contactName: z.string().min(2, "Contact name must be at least 2 characters"),
  contactPhone: z.string().optional(),
  contactDepartment: z.string().optional(),
  dueDate: z.string().optional(),
});

type CreateTicketFormValues = z.infer<typeof createTicketSchema>;

export default function TicketCreatePage() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [selectedCategoryId, setSelectedCategoryId] = useState<string>("");
  const [file, setFile] = useState<File | null>(null);
  const [searchAgent, setSearchAgent] = useState("");
  const [, navigate] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();
  const isMobile = window.innerWidth < 768;

  // Initialize form
  const form = useForm<CreateTicketFormValues>({
    resolver: zodResolver(createTicketSchema),
    defaultValues: {
      title: "",
      description: "",
      categoryId: "",
      subcategoryId: "",
      priority: "medium",
      supportType: "remote",
      contactEmail: "",
      contactName: "",
      contactPhone: "",
      contactDepartment: "",
      dueDate: "",
    },
  });

  // Fetch categories
  const { data: categories, isLoading: isLoadingCategories } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
    enabled: !!user,
  });

  // Get subcategories for selected category
  const { data: subcategories, isLoading: isLoadingSubcategories } = useQuery<Category[]>({
    queryKey: [`/api/categories/${selectedCategoryId}/subcategories`],
    enabled: !!selectedCategoryId && selectedCategoryId !== "",
  });

  // Fetch all users (agents and admins)
  const { data: allUsers, isLoading: isLoadingUsers } = useQuery<User[]>({
    queryKey: ["/api/users"],
    enabled: !!user,
  });

  // Filter agents and admins for contact field
  const agentUsers = allUsers?.filter(u => u.role === "agent" || u.role === "admin") || [];

  // Filter agents based on search
  const filteredAgents = agentUsers.filter(agent => 
    agent.name.toLowerCase().includes(searchAgent.toLowerCase()) ||
    agent.email.toLowerCase().includes(searchAgent.toLowerCase())
  );

  // Auto-fetch user details by email
  const handleEmailSelection = async (email: string) => {
    const selectedAgent = agentUsers.find(agent => agent.email === email);
    if (selectedAgent) {
      form.setValue("contactName", selectedAgent.name);
      form.setValue("contactPhone", selectedAgent.contactNumber || "");
      form.setValue("contactDepartment", selectedAgent.department || "");
    }
  };

  // Create ticket mutation
  const createTicketMutation = useMutation({
    mutationFn: async (data: CreateTicketFormValues) => {
      const res = await apiRequest("POST", "/api/tickets", {
        title: data.title,
        description: data.description,
        categoryId: parseInt(data.categoryId),
        subcategoryId: data.subcategoryId ? parseInt(data.subcategoryId) : undefined,
        priority: data.priority,
        supportType: data.supportType,
        contactEmail: data.contactEmail,
        contactName: data.contactName,
        contactPhone: data.contactPhone,
        contactDepartment: data.contactDepartment,
        dueDate: data.dueDate ? new Date(data.dueDate).toISOString() : undefined,
        status: "open",
      });
      return await res.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Ticket created successfully",
        description: `Ticket #${data.id} has been submitted.`,
      });
      navigate(`/tickets/${data.id}`);
    },
    onError: (error) => {
      toast({
        title: "Failed to create ticket",
        description: error.message || "An error occurred while creating the ticket.",
        variant: "destructive",
      });
    },
  });

  // Handle form submission
  const onSubmit = (data: CreateTicketFormValues) => {
    createTicketMutation.mutate(data);
  };

  // Handle category change
  const handleCategoryChange = (value: string) => {
    setSelectedCategoryId(value);
    form.setValue("categoryId", value);
    form.setValue("subcategoryId", "");
  };

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar for larger screens, or as a slide-over for mobile */}
      <Sidebar isMobile={isMobile} isOpen={sidebarOpen} onClose={() => setSidebarOpen(false)} />

      {/* Main content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header toggleSidebar={() => setSidebarOpen(!sidebarOpen)} title="Create Ticket" />

        {/* Main content scrollable area */}
        <main className="flex-1 overflow-y-auto p-4 md:p-6">
          <div className="flex items-center mb-6">
            <Button variant="ghost" size="sm" className="mr-2" onClick={() => navigate("/tickets")}>
              <ArrowLeft className="h-4 w-4" />
            </Button>
            <h2 className="text-xl font-semibold text-gray-800">Create New Support Ticket</h2>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>New Support Request</CardTitle>
              <CardDescription>
                Provide detailed information about your issue for faster resolution
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Ticket Title</FormLabel>
                        <FormControl>
                          <Input placeholder="Brief summary of the issue" {...field} />
                        </FormControl>
                        <FormDescription>
                          Be concise and specific about the problem
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-5">
                    <FormField
                      control={form.control}
                      name="categoryId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Category</FormLabel>
                          <Select
                            onValueChange={(value) => handleCategoryChange(value)}
                            value={field.value}
                            disabled={isLoadingCategories}
                          >
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select Category" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {categories?.filter(c => !c.parentId).map((category) => (
                                <SelectItem key={category.id} value={category.id.toString()}>
                                  {category.name}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="subcategoryId"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Subcategory</FormLabel>
                          <Select
                            onValueChange={field.onChange}
                            value={field.value}
                            disabled={isLoadingSubcategories || !selectedCategoryId}
                          >
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select Subcategory" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              {subcategories?.map((subcategory) => (
                                <SelectItem key={subcategory.id} value={subcategory.id.toString()}>
                                  {subcategory.name}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="priority"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Priority</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select Priority" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="low">Low</SelectItem>
                              <SelectItem value="medium">Medium</SelectItem>
                              <SelectItem value="high">High</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
                    <FormField
                      control={form.control}
                      name="supportType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Support Type</FormLabel>
                          <Select onValueChange={field.onChange} value={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Select Support Type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="remote">Remote</SelectItem>
                              <SelectItem value="telephonic">Telephonic</SelectItem>
                              <SelectItem value="onsite_visit">Onsite Visit</SelectItem>
                              <SelectItem value="other">Other</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="dueDate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Due Date (Optional)</FormLabel>
                          <FormControl>
                            <Input type="date" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="space-y-4">
                    <h3 className="text-lg font-medium text-gray-800">Contact Information</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
                      <FormField
                        control={form.control}
                        name="contactEmail"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Contact Email</FormLabel>
                            <Select
                              onValueChange={(value) => {
                                field.onChange(value);
                                handleEmailSelection(value);
                              }}
                              value={field.value}
                              disabled={isLoadingUsers}
                            >
                              <FormControl>
                                <SelectTrigger>
                                  <SelectValue placeholder="Select Agent or Enter Email" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <div className="p-2">
                                  <Input
                                    placeholder="Search by name or email..."
                                    value={searchAgent}
                                    onChange={(e) => setSearchAgent(e.target.value)}
                                    className="mb-2"
                                  />
                                </div>
                                {filteredAgents.length > 0 ? (
                                  filteredAgents.map((agent) => (
                                    <SelectItem key={agent.id} value={agent.email}>
                                      {agent.name} ({agent.email})
                                    </SelectItem>
                                  ))
                                ) : (
                                  <SelectItem value="" disabled>
                                    No agents found
                                  </SelectItem>
                                )}
                              </SelectContent>
                            </Select>
                            <FormDescription>
                              Select an agent from the list or manually enter details below
                            </FormDescription>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="contactName"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Contact Name</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter contact name" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="contactPhone"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Contact Phone (Optional)</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter phone number" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="contactDepartment"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Contact Department (Optional)</FormLabel>
                            <FormControl>
                              <Input placeholder="Enter department" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea
                            rows={6}
                            placeholder="Detailed description of the issue you're experiencing..."
                            {...field}
                          />
                        </FormControl>
                        <FormDescription>
                          Include relevant details like error messages, when it started, and what you've tried
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="border border-dashed border-gray-300 rounded-md p-6">
                    <div className="text-center">
                      <FilePlus className="mx-auto h-12 w-12 text-gray-400" />
                      <div className="mt-4">
                        <label
                          htmlFor="file-upload"
                          className="relative cursor-pointer rounded-md bg-white font-medium text-primary hover:text-primary-dark focus-within:outline-none"
                        >
                          <span>Upload a file</span>
                          <input
                            id="file-upload"
                            name="file-upload"
                            type="file"
                            className="sr-only"
                            onChange={e => {
                              if (e.target.files && e.target.files[0]) {
                                setFile(e.target.files[0]);
                              }
                            }}
                            accept=".png,.jpg,.jpeg,.pdf,.doc,.docx"
                          />
                        </label>
                        <p className="pl-1 text-sm text-gray-500">or drag and drop</p>
                        {file && (
                          <p className="text-xs text-green-600 mt-2">
                            Selected file: {file.name}
                          </p>
                        )}
                      </div>
                      <p className="text-xs text-gray-500 mt-2">
                        PNG, JPG, PDF, DOC up to 10MB
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center justify-end space-x-3 pt-4">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => navigate("/tickets")}
                    >
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createTicketMutation.isPending}
                    >
                      {createTicketMutation.isPending ? "Creating..." : "Create Ticket"}
                    </Button>
                  </div>
                </form>
              </Form>
            </CardContent>
          </Card>
        </main>
      </div>
    </div>
  );
}

