                      <SelectValue placeholder="All Statuses" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all-statuses">All Statuses</SelectItem>
                      <SelectItem value="open">Open</SelectItem>
                      <SelectItem value="in-progress">In Progress</SelectItem>
                      <SelectItem value="resolved">Resolved</SelectItem>
                      <SelectItem value="closed">Closed</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="w-full sm:w-auto">
                  <label className="block text-sm font-medium text-gray-700 mb-1">Priority</label>
                  <Select 
                    value={filters.priority || "all-priorities"} 
                    onValueChange={(value) => setFilters({...filters, priority: value !== "all-priorities" ? value : undefined})}
                  >
                    <SelectTrigger className="w-full sm:w-40">
                      <SelectValue placeholder="All Priorities" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all-priorities">All Priorities</SelectItem>
                      <SelectItem value="high">High</SelectItem>
                      <SelectItem value="medium">Medium</SelectItem>
                      <SelectItem value="low">Low</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="w-full sm:w-auto">
                  <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                  <Select 
                    value={filters.categoryId?.toString() || "all-categories"} 
                    onValueChange={(value) => setFilters({
                      ...filters, 
                      categoryId: value !== "all-categories" ? parseInt(value) : undefined
                    })}
                  >
                    <SelectTrigger className="w-full sm:w-48">
                      <SelectValue placeholder="All Categories" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all-categories">All Categories</SelectItem>
                      {categories?.filter(c => !c.parentId).map((category) => (
                        <SelectItem key={category.id} value={category.id.toString()}>
                          {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="w-full sm:w-auto ml-auto flex items-end gap-2">
                  <Button variant="outline" onClick={handleResetFilters}>
                    Reset
                  </Button>
                  <Button variant="outline" onClick={handleApplyFilters}>
                    <Filter className="mr-2 h-4 w-4" />
                    Apply Filters
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Tickets List */}
          <Card>
            {isLoadingTickets ? (
              <CardContent className="p-6">
                <div className="space-y-4">
                  {[...Array(5)].map((_, index) => (
                    <Skeleton key={index} className="h-16 w-full" />
                  ))}
                </div>
              </CardContent>
            ) : filteredTickets && filteredTickets.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Title</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Priority</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Created</th>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {filteredTickets.map((ticket) => (
                      <tr key={ticket.id} className="hover:bg-gray-50">
                        <td className="px-4 py-4 whitespace-nowrap">
                          <span className="text-sm font-mono text-gray-900">TKT-{ticket.id.toString().padStart(4, '0')}</span>
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap">
                          <Link href={`/tickets/${ticket.id}`}>
                            <a className="text-sm font-medium text-blue-600 hover:text-blue-800">
                              {ticket.title}
                            </a>
                          </Link>
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap">
                          <Badge variant="outline" className={getStatusColor(ticket.status)}>
                            {ticket.status.charAt(0).toUpperCase() + ticket.status.slice(1)}
                          </Badge>
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap">
                          <span className={`text-sm capitalize ${getPriorityColor(ticket.priority)}`}>
                            {ticket.priority}
                          </span>
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm text-gray-500">
                          {formatDate(ticket.createdAt)}
                        </td>
                        <td className="px-4 py-4 whitespace-nowrap text-sm font-medium">
                          <div className="flex space-x-2">
                            <Button variant="ghost" size="icon" asChild>
                              <Link href={`/tickets/${ticket.id}`}>
                                <Eye className="h-4 w-4 text-blue-600" />
                              </Link>
                            </Button>
                            <Button variant="ghost" size="icon">
                              <MessageSquare className="h-4 w-4 text-gray-600" />
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <CardContent className="p-12 text-center">
                <h3 className="text-lg font-medium text-gray-900 mb-2">No tickets found</h3>
                <p className="text-gray-500 mb-6">
                  {filters.status || filters.priority || filters.categoryId || searchQuery
                    ? "Try adjusting your filters or search query"
                    : "You haven't created any support tickets yet"}
                </p>
                <Button asChild>
                  <Link href="/tickets/new">
                    <Plus className="mr-2 h-4 w-4" />
                    Create your first ticket
                  </Link>
                </Button>
              </CardContent>
            )}

            {/* Pagination (simplified) */}
            {filteredTickets && filteredTickets.length > 0 && (
              <div className="px-4 py-3 flex items-center justify-between border-t border-gray-200">
                <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
                  <div>
                    <p className="text-sm text-gray-700">
                      Showing <span className="font-medium">1</span> to{" "}
                      <span className="font-medium">{filteredTickets.length}</span> of{" "}
                      <span className="font-medium">{filteredTickets.length}</span> results
                    </p>
                  </div>
                  {/* Pagination controls would go here in a real app */}
                </div>
              </div>
            )}
          </Card>
        </main>
      </div>
    </div>
  );
}

client/src/components/chatbot/chatbot.ts.content:
import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { X, MessageSquare, Send, User, Bot } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { 
  useQuery, 
  useMutation, 
  useQueryClient 
} from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";

interface ChatbotProps {
  isOpen: boolean;
  onClose: () => void;
}

type Message = {
  id: number;
  userId: number;
  message: string;
  isFromBot: boolean;
  createdAt: string;
};

export default function Chatbot({ isOpen, onClose }: ChatbotProps) {
  const [message, setMessage] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch chat history
  const { data: chatMessages, isLoading } = useQuery<Message[]>({
    queryKey: ["/api/chat"],
    queryFn: undefined, // Uses the default fetcher
    enabled: isOpen && !!user, // Only fetch when chatbot is open and user is logged in
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (newMessage: string) => {
      const res = await apiRequest("POST", "/api/chat", { message: newMessage });
      return await res.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData(["/api/chat"], (oldData: Message[] = []) => [...oldData, ...data]);
      scrollToBottom();
    },
    onError: (error) => {
      toast({
        title: "Failed to send message",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [chatMessages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!message.trim() || !user) return;

    sendMessageMutation.mutate(message);
    setMessage("");
  };

  // Chatbot toggle button (fixed position)
  const ChatbotToggle = () => (
    <Button
      className="fixed bottom-4 right-4 z-40 rounded-full w-14 h-14 shadow-lg p-0"
      onClick={isOpen ? onClose : undefined}
    >
      <MessageSquare size={24} />
    </Button>
  );

  if (!isOpen) {
    return <ChatbotToggle />;
  }

  return (
    <>
      <ChatbotToggle />

      <div className="fixed bottom-20 right-4 z-40 w-80 shadow-lg">
        <Card className="flex flex-col h-96 max-h-96">
          <CardHeader className="bg-primary text-white py-3 px-4 flex flex-row items-center justify-between space-y-0">
            <CardTitle className="text-base font-medium flex items-center">
              <Bot className="h-5 w-5 mr-2" />
              IT Support Assistant
            </CardTitle>
            <Button
              variant="ghost"
              size="icon"
              className="text-white hover:bg-primary-foreground/10 h-8 w-8"
              onClick={onClose}
            >
              <X size={16} />
            </Button>
          </CardHeader>

          <CardContent className="flex-1 overflow-y-auto p-4 space-y-4">
            {isLoading ? (
              <div className="flex justify-center items-center h-full">
                <p className="text-sm text-gray-500">Loading messages...</p>
              </div>
            ) : !chatMessages || chatMessages.length === 0 ? (
              <div className="flex flex-col justify-center items-center h-full text-center space-y-2">
                <Bot className="h-10 w-10 text-gray-400" />
                <p className="text-sm text-gray-500">Hello! I'm your IT support assistant.</p>
                <p className="text-sm text-gray-500">How can I help you today?</p>
              </div>
            ) : (
              chatMessages.map((msg, index) => (
                <div
                  key={msg.id || index}
                  className={`flex ${msg.isFromBot ? "justify-start" : "justify-end"}`}
                >
                  <div
                    className={`max-w-[80%] rounded-lg px-3 py-2 ${
                      msg.isFromBot
                        ? "bg-gray-100 text-gray-800"
                        : "bg-primary text-white"
                    }`}
                  >
                    <div className="flex items-center mb-1">
                      {msg.isFromBot ? (
                        <Bot className="h-3 w-3 mr-1" />
                      ) : (
                        <User className="h-3 w-3 mr-1" />
                      )}
                      <span className="text-xs">
                        {msg.isFromBot ? "Assistant" : "You"}
                      </span>
                    </div>
                    <p className="text-sm break-words">{msg.message}</p>
                  </div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </CardContent>

          <CardFooter className="border-t p-3">
            <form onSubmit={handleSendMessage} className="flex w-full gap-2">
              <Input
                placeholder="Type your message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                className="flex-1"
                disabled={sendMessageMutation.isPending || !user}
              />
              <Button
                type="submit"
                size="icon"
                disabled={sendMessageMutation.isPending || !message.trim() || !user}
              >
                <Send size={16} />
              </Button>
            </form>
          </CardFooter>
        </Card>

        {!user && (
          <div className="mt-2 text-xs text-center p-2 bg-white rounded shadow-md">
            <p className="text-gray-700">
              Please <Link href="/auth"><a className="text-primary hover:underline">login</a></Link> to chat with support
            </p>
          </div>
        )}
      </div>
    </>
  );
}

