import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { 
  Headset, 
  Home, 
  TicketCheck, 
  Book, 
  FileText,
  ListChecks, 
  Users, 
  Tags, 
  BarChart, 
  Settings, 
  Menu, 
  X 
} from "lucide-react";

interface SidebarProps {
  isMobile?: boolean;
  isOpen?: boolean;
  onClose?: () => void;
}

export default function Sidebar({ isMobile = false, isOpen = true, onClose }: SidebarProps) {
  const [location] = useLocation();
  const { user, logoutMutation } = useAuth();

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  // Close sidebar on location change in mobile view
  useEffect(() => {
    if (isMobile && isOpen && onClose) {
      onClose();
    }
  }, [location, isMobile, isOpen, onClose]);

  // Check if route is active
  const isRouteActive = (route: string) => {
    if (route === "/") return location === "/";
    return location.startsWith(route);
  };

  return (
    <div
      className={cn(
        "bg-white h-full fixed inset-y-0 left-0 z-30 w-64 shadow-lg flex flex-col",
        "md:relative md:translate-x-0",
        isMobile && "transform transition-transform duration-300 ease-in-out",
        isMobile && (isOpen ? "translate-x-0" : "-translate-x-full")
      )}
    >
      {/* Logo & Close Button */}
      <div className="flex items-center justify-between px-4 h-16 bg-primary text-white">
        <Link href="/" className="flex items-center space-x-2">
          <Headset size={24} />
          <span className="font-semibold text-lg">IT Helpdesk</span>
        </Link>
        {isMobile && (
          <Button
            variant="ghost"
            size="icon"
            onClick={onClose}
            className="text-white hover:bg-primary-foreground/10"
          >
            <X size={20} />
          </Button>
        )}
      </div>

      {/* User Info */}
      <div className="p-4 border-b">
        <div className="flex items-center space-x-3">
          <div className="bg-gray-200 w-10 h-10 rounded-full flex items-center justify-center">
            <Users className="h-5 w-5 text-gray-600" />
          </div>
          <div>
            <p className="font-medium">{user?.name || "Guest"}</p>
            <p className="text-sm text-gray-500 capitalize">{user?.role || "Not logged in"}</p>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 overflow-y-auto p-2">
        <div className="text-xs font-semibold text-gray-500 uppercase tracking-wider px-3 py-2">
          Main
        </div>
        <SidebarLink href="/" active={isRouteActive("/")} icon={<Home size={18} />} label="Dashboard" />
        <SidebarLink
          href="/tickets"
          active={isRouteActive("/tickets")}
          icon={<TicketCheck size={18} />}
          label="My Tickets"
        />
        <SidebarLink
          href="/knowledge-base"
          active={isRouteActive("/knowledge-base")}
          icon={<Book size={18} />}
          label="Knowledge Base"
        />
        <SidebarLink
          href="/documentation"
          active={isRouteActive("/documentation")}
          icon={<FileText size={18} />}
          label="Documentation"
        />

        {/* Support Staff Section (Now visible to all users) */}
        <>
          <div className="text-xs font-semibold text-gray-500 uppercase tracking-wider px-3 pb-1 pt-4">
            Support
          </div>
          <SidebarLink
            href="/all-tickets"
            active={isRouteActive("/all-tickets")}
            icon={<ListChecks size={18} />}
            label="All Tickets"
          />
        </>

        {/* Admin Section (Now visible to all users) */}
        <>
          <div className="text-xs font-semibold text-gray-500 uppercase tracking-wider px-3 pb-1 pt-4">
            Administration
          </div>
          <SidebarLink
            href="/admin/users"
            active={isRouteActive("/admin/users")}
            icon={<Users size={18} />}
            label="Users"
          />
          <SidebarLink
            href="/admin/categories"
            active={isRouteActive("/admin/categories")}
            icon={<Tags size={18} />}
            label="Categories"
          />
          <SidebarLink
            href="/admin/reports"
            active={isRouteActive("/admin/reports")}
            icon={<BarChart size={18} />}
            label="Reports"
          />
        </>

        {/* Personal Section */}
        <>
          <div className="text-xs font-semibold text-gray-500 uppercase tracking-wider px-3 pb-1 pt-4">
            Personal
          </div>
          <SidebarLink
            href="/settings"
            active={isRouteActive("/settings")}
            icon={<Settings size={18} />}
            label="Settings"
          />
        </>
      </nav>

      {/* Logout */}
      <div className="p-4 border-t">
        <Button
          variant="outline"
          className="w-full justify-start"
          onClick={handleLogout}
          disabled={logoutMutation.isPending}
        >
          {logoutMutation.isPending ? "Logging out..." : "Logout"}
        </Button>
      </div>
    </div>
  );
}

interface SidebarLinkProps {
  href: string;
  label: string;
  icon: React.ReactNode;
  active: boolean;
}

function SidebarLink({ href, label, icon, active }: SidebarLinkProps) {
  return (
    <Link href={href}>
      <div
        className={cn(
          "flex items-center px-3 py-2 text-sm font-medium rounded-md mb-1 transition-colors cursor-pointer",
          active
            ? "bg-primary text-white"
            : "text-gray-700 hover:bg-gray-100"
        )}
      >
        <span className="mr-2">{icon}</span>
        {label}
      </div>
    </Link>
  );
}

client/src/components/tickets/comment-th.content:
import { User, Shield, MessageSquare } from "lucide-react";
import { Badge } from "@/components/ui/badge";

interface CommentThreadProps {
  comments: {
    id: number;
    content: string;
    createdAt: string;
    isInternal: boolean;
    user: {
      id: number;
      name: string;
      role: string;
    };
  }[];
  currentUserRole: string;
}

export default function CommentThread({ comments, currentUserRole }: CommentThreadProps) {
  // Format date
  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  // Filter out internal comments if user is not admin or agent
  const visibleComments = comments.filter(comment => 
    !comment.isInternal || 
    currentUserRole === "admin" || 
    currentUserRole === "agent"
  );

  if (visibleComments.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow p-6 text-center text-gray-500">
        No comments yet.
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {visibleComments.map((comment, index) => (
        <div 
          key={comment.id} 
          className={`bg-white rounded-lg shadow p-6 ${comment.user.role === 'agent' || comment.user.role === 'admin' ? 'bg-blue-50' : ''}`}
          id={`comment-${comment.id}`}
        >
          <div className="flex justify-between items-start mb-4">
            <div className="flex items-start">
              <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-3 ${
                comment.user.role === 'agent' || comment.user.role === 'admin' 
                  ? 'bg-blue-100 text-blue-600' 
                  : 'bg-gray-200 text-gray-500'
              }`}>
                {comment.user.role === 'agent' || comment.user.role === 'admin' 
                  ? <Shield className="h-5 w-5" /> 
                  : <User className="h-5 w-5" />}
              </div>
              <div>
                <h3 className="font-medium text-gray-800">{comment.user.name}</h3>
                <div className="flex items-center">
                  <p className="text-sm text-gray-500">{formatDate(comment.createdAt)}</p>
                  {(comment.user.role === 'agent' || comment.user.role === 'admin') && (
                    <Badge className="ml-2 bg-blue-100 text-blue-800 hover:bg-blue-100">
                      {comment.user.role === 'agent' ? 'Support Agent' : 'Admin'}
                    </Badge>
                  )}
                  {comment.isInternal && (
                    <Badge variant="outline" className="ml-2 bg-gray-800 text-white hover:bg-gray-800">
                      Internal Note
                    </Badge>
                  )}
                </div>
              </div>
            </div>
          </div>
          <div className="text-gray-700 whitespace-pre-wrap">
            {comment.content}
          </div>
        </div>
      ))}
    </div>
  );
}

